defmodule NumeroTest do
  use ExUnit.Case
  doctest Numero

  test "the truth" do
    assert 1 + 1 == 2
  end

  test "Combinational digit strings does not work digit_only?" do
    assert Numero.digit_only?("123۱۲۳۰0")
  end

